mapper.select:
select * from COMMUNITY BOARD

ORDER BY ${orderby} ${direct}
limit ${startIdx}, ${pageRow}

mapper.insert:
INSERT INTO COMMUNITY BOARD (
	cst_no, cst_grade, cst_id, cb_regdate, cb_title, cd_content
) VALUES (
	#{cst_no},
	#{cst_grade},
	#{cst_id},
	NOW(),
	#{cb_title},
	#{cd_content}
)

mapper.update:
UPDATE COMMUNITY BOARD SET
	cb_title = #{cb_title}
	, cd_content = #{cd_content}
	
	
WHERE
	cb_no = #{cb_no}
