mapper.select:
select *,
	(select count(*) from bb_comment where bb_no = bulletin_board.bb_no) as commentCount,
	ifnull((select cst_name from customer where cst_no=bulletin_board.cst_no),'관리자') as user_name
 from bulletin_board
		
ORDER BY ${orderby} ${direct}
limit ${startIdx}, ${pageRow}

mapper.insert:
INSERT INTO bulletin_board (
	cst_no, cst_grade, cst_id, bb_regdate, bb_title, bb_content
) VALUES (		
	1,
	1,
	'test_id',	
	NOW(),
	#{bb_title},
	#{bb_content}			
)

mapper.update:
UPDATE bulletin_board SET
	bb_title = #{bb_title}
	, bb_content = #{bb_content}			
WHERE
	bb_no = #{bb_no}
