mapper.select:
select * from bulletin_board	
ORDER BY ${orderby} ${direct}
limit ${startIdx}, ${pageRow}

mapper.insert:
INSERT INTO bulletin_board (
	cst_no, cst_grade, cst_id, bb_regdate, bb_title, bb_content
) VALUES (		
	1,
	1,
	'test_id',	
	NOW(),
	#{bb_title},
	#{bb_content}			
)

mapper.update:
UPDATE bulletin_board SET
	bb_title = #{bb_title}
	, bb_content = #{bb_content}			
WHERE
	bb_no = #{bb_no}
