mapper.select:
select * from product

ORDER BY pd_no DESC
limit ${startIdx}, ${pageRow}

mapper.update:
UPDATE product SET
	 pd_name = #{pd_name}
	, pd_price = #{pd_price}
	, pd_content = #{pd_content}
	, pd_category = #{pd_category}
	, pd_info = #{pd_info}
	
	
	, pd_image=#{pd_image}
	
WHERE
	pd_no = #{pd_no}
